// Visit The Stimulus Handbook for more details 
// https://stimulusjs.org/handbook/introduction
// 
// This example controller works with specially annotated HTML like:
//
// <div data-controller="hello">
//   <h1 data-target="hello.output"></h1>
// </div>

import { Controller } from "stimulus"
import { ReactiveRenderer } from "../lib/reactive_renderer.js"

export default class extends Controller {
  static targets = [ "output", "baz", "counter" ]

  initialize() {
    this.output = "Jolt works!!! :)"
    this.counter = 0
    this.numberOfTimesRendered = 0
  }

  connect() {
    this.baz = "Loading…"
    this.renderer = new ReactiveRenderer(this)
    setTimeout(() => {
      this.output = "Loaded!"
      this.baz = "Done."
    }, 2000)
  }

  render() {  
    this.renderTargets({
      output: [this.output, {class: 'foo', toggle: !this.outputTarget.classList.contains('foo')}],
      baz: this.baz,
      counter: this.counter
    })
    this.numberOfTimesRendered += 1
//    console.log("RENDERED", this.numberOfTimesRendered)
  }

  incrementCounter() {
    this.counter += 1
  }
}
